&#39;) 10 while textList[-1] != &#39;#EXT-X-ENDLIST&#39;: 11 textList.pop(-1) 12 for i in textList: 13 if i[0] != &#39;#&#39;: 14 tsList.append(tempUrl + i) 15 global tsCnt 16 tsCnt = len(tsList) 下載ts檔 1q = queue.Queue() 2 3def downloadts(): 4 global tsList 5 while q.qsize() &gt; 0: 6 num = q.get() 7 r = requests.get(tsList[num]) 8 with open(&#39;./ts/&#39; + str(num+1) + &#39;.ts&#39;, &#39;wb&#39;) as f: 9 f.write(r.content) 10 11if __name__ == &#34;__main__&#34;: 12 if not os.path.isdir(&#39;./ts/&#39;): 13 os.mkdir(&#39;./ts/&#39;) 14 for i in range(tsCnt): 15 q.put(i) 16 thList = [] 17 for i in range(tsCnt): 18 th = threading.Thread(target=downloadts) 19 th.start() 20 thList.append(th) 21 for th in thList: 22 th.join() 23 print(&#39;下載ts完成&#39;) 合併ts成mp4 1def merge(tsCnt, output): 2 for i in range(tsCnt): 3 with open(&#39;./ts/ts.txt&#39;, &#39;a+&#39;) as f: 4 f.write(&#39;file &#39; + str(i+1) + &#39;.ts
&#39;) 5 command = &#39;ffmpeg -y -f concat -i %s-bsf:a aac_adtstoasc -c copy %s&#39; % (&#39;./ts/ts.txt&#39;, output) 6 os.system(command) 7 print(&#39;合併成功&#39;) 移除無用檔案 1def remove(): 2 shutil.rmtree(&#39;./ts/&#39;) 3 os.remove(&#39;./index.m3u8&#39;)  完整程式碼 1# -*- coding: UTF-8 -*- 2import requests, os, threading, queue, shutil 3 4m3u8Url = &#39;https://.../index.m3u8&#39; 5tsList = [] 6tsCnt = 0 7q = queue.Queue() 8 9def downloadM3u8(url): 10 r = requests.get(url) 11 with open(&#39;./index.m3u8&#39;, &#39;wb&#39;) as f: 12 f.write(r.content) 13 14def analyzeM3u8(): 15 tsList.clear() 16 tempUrl = m3u8Url.rsplit(&#39;/&#39;, 1)[0] + &#39;/&#39; 17 with open(&#39;./index.m3u8&#39;, &#39;r&#39;) as f: 18 text = f.read() 19 textList = text.split(&#39;
&#39;) 20 while textList[-1] != &#39;#EXT-X-ENDLIST&#39;: 21 textList.pop(-1) 22 for i in textList: 23 if i[0] != &#39;#&#39;: 24 tsList.append(tempUrl + i) 25 global tsCnt 26 tsCnt = len(tsList) 27 28def downloadts(): 29 global tsList 30 while q.qsize() &gt; 0: 31 num = q.get() 32 r = requests.get(tsList[num]) 33 with open(&#39;./ts/&#39; + str(num+1) + &#39;.ts&#39;, &#39;wb&#39;) as f: 34 f.write(r.content) 35 36def merge(tsCnt, output): 37 for i in range(tsCnt): 38 with open(&#39;./ts/ts.txt&#39;, &#39;a+&#39;) as f: 39 f.write(&#39;file &#39; + str(i+1) + &#39;.ts
&#39;) 40 command = &#39;ffmpeg -y -f concat -i %s-bsf:a aac_adtstoasc -c copy %s&#39; % (&#39;./ts/ts.txt&#39;, output) 41 os.system(command) 42 print(&#39;合併成功&#39;) 43 44def remove(): 45 shutil.rmtree(&#39;./ts/&#39;) 46 os.remove(&#39;./index.m3u8&#39;) 47 48if __name__ == &#34;__main__&#34;: 49 downloadM3u8(m3u8Url) 50 analyzeM3u8() 51 print(&#39;m3u8下載且分析完畢&#39;) 52 if not os.path.isdir(&#39;./ts/&#39;): 53 os.mkdir(&#39;./ts/&#39;) 54 for i in range(tsCnt): 55 q.put(i) 56 thList = [] 57 for i in range(tsCnt): 58 th = threading.Thread(target=downloadts) 59 th.start() 60 thList.append(th) 61 for th in thList: 62 th.join() 63 print(&#39;下載ts完成&#39;) 64 merge(tsCnt, &#39;./test.mp4&#39;) 65 remove()
